import React from 'react';
import { StyleSheet, View, Text, FlatList, SafeAreaView } from 'react-native';
import { stylePatterns } from '../../utils/stylesPatterns';
import { RoundedButton } from '../../components/RoundedButton';

export const HistoryItem = ({item, index}) => {
  return(
    <Text style={styles.historyItem(item.status)}>
      {JSON.stringify(item.subject)}
    </Text>
  );
};

export const FocusHistory = ({focusHistory, onClear}) => {
  const clearHistory = () => {
    onClear();
  };
  return(
    <>
    <SafeAreaView style={{flex: 0.5}}>
      <Text style={styles.title}>
        Things we have focused on
        {focusHistory.length && 
          <FlatList
           style={{flex: 1}}
           contentContainerStyle={{flex: 1, alignItems: 'center', flexDirection: 'column'}}
           data={focusHistory}
           renderItem={HistoryItem} 
          //  keyExtractor={}
          />
        }
      </Text>
    </SafeAreaView>
    </>
  );
};

const styles = StyleSheet.create({
  historyItem: (status) => ({
    color: status > 1 ? 'red' : 'green',
    fontSize: stylePatterns.fontSizes.md
  }),
  title: {
    color: stylePatterns.color.white,
    fontSize: stylePatterns.fontSizes.lg
  }
});
