import React, { useState } from 'react';
import { View, StyleSheet, Text } from 'react-native';
import { stylePatterns } from '../../utils/stylesPatterns';
import { Countdown } from '../../components/Countdown';
import { RoundedButton } from '../../components/RoundedButton';

export const Timer = ({ focusSubject }) => {
  const [isStarted, setIsStarted ] = useState(false);

  return (
    <View style={styles.container}>
      <View style={styles.countdown}>
        <Countdown isPaused={!isStarted}/>
      </View>
      <View style={{ paddingTop: stylePatterns.paddingSizes.xxl }}>
        <Text style={styles.title}>We are focusing on</Text>
        <Text style={styles.task}>{focusSubject}</Text>
      </View>
      <RoundedButton title='start' size={50} onPress={()=> setIsStarted(true)}/>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 0.5,
  },
  title: {
    color: stylePatterns.color.white,
    textAlign: "center"
  },
  task: {
    fontWeight: 'bold',
    textAlign: "center",
    color: stylePatterns.color.white
  },
  countdown: {
    flex: 0.5,
    alignItems: "center",
    justifyContent: "center"
  }
});
